import React from "react";
import Button from '@material-ui/core/Button';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from "@material-ui/core/styles";
import { connect } from "react-redux";
import { addProduct, addItem} from '../store/actions'
const useStyle = makeStyles((theme) => ({
  cardGrid: {
    paddingTop: theme.spacing(8),
    paddingBottom: theme.spacing(8),
  },
  card: {
    height: "100%",
    display: "flex",
    flexDirection: "column",
  },
  cardMedia: {
    paddingTop: "56.25%",
  },
  cardContent: {
    flexGrow: 1,
  },
}));
const Products = (props) => {
const classes = useStyle();
  return (
    <section>
      {props.products.map((product) => {
        if (props.activeCategory===product.category) {
          return (
            <Grid item key={product.name} xs={12} sm={6} md={4}>
              <Card className={classes.card}>
                <CardMedia
                  className={classes.cardMedia}
                  image={product.img}
                  title={product.name}
                />
                <CardContent className={classes.cardContent}>
                  <Typography gutterBottom variant="h5" component="h2">
                    {product.name}
                  </Typography>
                  <Typography>
                    Category: {products.category} <br />
                    Price: {products.price} <br />
                    Inventory: {products.inventoryCount}
                  </Typography>
                </CardContent>
                <CardActions>
                  {/* <Button size="small" color="primary">
                    View
                  </Button> */}
                  <Button size="small" color="primary"
                  onClick={props.addItem}>
                    Add to Cart
                  </Button>
                </CardActions>
              </Card>
            </Grid>
          );
        }

      })}
    </section>
  );
};

const mapStateToProps = (state) => ({
  activeCategory: state.categoriesReducer.activeCategory,
  products: state.productsReducer.activatedProduct,

})
const mapDispatchToProps = { addProduct, addItem };

export default connect(mapStateToProps, mapDispatchToProps)(products);
