export const addItem = (item) => {
    return (
        {
            type: 'ADD_ITEM',
            payload: item
        }
    )
}

export const resetCart = () => {
    return ({
        type: 'REST_CART'
    }
   )
}

// category actions
export const activatedCategory = (category) => {
    return {
        type: 'ACTIVE',
        payload: category,
    };
}
export const reset = () => {
    return {
        type: 'RESET',

    };
}

// product actions
export const activatedProduct = (name) => {
    return {
        type: 'ACTIVE',
        payload: name,
    };
}
